
public class practica6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		int suma =0;

		for(int contador=3;contador<=30; contador= contador+3) {
			if (contador==3) {
				System.out.print(contador);
			}
			else {
			System.out.print("+");
			System.out.print(contador);
			}
			
			
			suma = suma + contador;
			
		}
			
		System.out.println("\n"+ suma);



		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
