package While;

public class while2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			int resultadoSuma = 0;
			int contador = 1; 

		 while (contador<=10000) {
			
			 resultadoSuma += contador;
			contador++;
		
		 }
	
		 System.out.println("La suma total "+ resultadoSuma );
			
		
		
		
		
		
		
	}

}
